import {
    TAREAS_PROYECTO,
    AGREGAR_TAREA,
    VALIDAR_TAREA,
    ELIMINAR_TAREA,
    ESTADO_TAREA
} from '../../types';

export default (state, action) =>{
    switch(action.type){
        case TAREAS_PROYECTO:
            return {
                ...state,
                tareasproyecto: state.tareas.filter(tarea => tarea.proyectoId === 
                    action.payload)
            }

        case AGREGAR_TAREA:
            return{
                ...state,
                tareas : [action.payload, ...state.tareas],
                //array nuevo de tareas mas la nueva
                errortarea: false
                //reseteamos el error si se hace bien para quitar el mensaje de alerta
            }
        
        case VALIDAR_TAREA:
            return{
                ...state,
                errortarea: true
            }

        case ELIMINAR_TAREA:
            return{
                ...state,
                tareas: state.tareas.filter( tarea => tarea.id !== action.payload )
            }
        
            case ESTADO_TAREA:
                return{
                    ...state,
                    tareas: state.tareas.map(tarea => tarea.id === action.payload.id )
                }

        default:
            return state;
    }
}
