import React, {useReducer} from 'react';
import TareaContext from './tareaContext';
import TareaReducer from './tareaReducer';
import {
    TAREAS_PROYECTO,
    AGREGAR_TAREA,
    VALIDAR_TAREA,
    ELIMINAR_TAREA,
    ESTADO_TAREA,
    TAREA_ACTUAL,
    ACTUALIZAR_TAREA
} from '../../types';
import clienteAxios from '../../config/axios';

const TareaState = props => {
    const initialState={
        tareasproyecto: [],
        errortarea: false,
        tareaseleccionada: null
    }

    //Crear dispatch y state
    const [state, dispatch] = useReducer(TareaReducer, initialState);


    //CREAR LAS FUNCIONES QUE NECESITAMOS DE LAS TAREAS

    //Obtener las tareas de un proyecto 
    const obtenerTareas = async proyecto =>{
        
        try {
            const resultado = await clienteAxios.get('/api/tareas',{ params: { proyecto }});
            dispatch({
                type: TAREAS_PROYECTO,
                payload: resultado.data.tareas
                
            });
        } catch (error) {
            console.log(error);
        }
    }

    // Anyadir una tarea al proyecto seleccionado
    const agregarTarea = async tarea=>{
        try {
            const resultado = await clienteAxios.post('/api/tareas', tarea);

            dispatch({
                type: AGREGAR_TAREA,
                payload: tarea
            });
        } catch (error) {
            console.log(error)
        }
    }

    //Validar y mostrar un error en caso de ser necesario
    const validarTarea = () =>{
        dispatch({
            type: VALIDAR_TAREA

        })
    }

    //Eliminar las tareas por el id
    const eliminarTarea = async (id, proyecto) => {
        try {
            await clienteAxios.delete(`/api/tareas/${id}`, {params: {proyecto}});
            dispatch({
                type: ELIMINAR_TAREA,
                payload: id
            });
        } catch (error) {
            console.log(error);
        }
    }

    

    //Cambiar el estado de las tareas
    const cambiarEstadoTarea = tarea => {
        dispatch({
            type: ESTADO_TAREA,
            payload: tarea
        })
    }

    //Extraer una tarea para la edicion
    const guardarTareaActual = tarea =>{
        dispatch({
            type: TAREA_ACTUAL,
            payload : tarea
        })
    }

    //edita o modifica una tarea
    const actualizarTarea = tarea => {
        dispatch({
            type: ACTUALIZAR_TAREA,
            payload : tarea
        })
    }

    return (
        <TareaContext.Provider
        value={{
            tareasproyecto : state.tareasproyecto,
            errortarea: state.errortarea,
            tareaseleccionada: state.tareaseleccionada,
            obtenerTareas,
            agregarTarea,
            validarTarea,
            eliminarTarea,
            cambiarEstadoTarea,
            guardarTareaActual,
            actualizarTarea
        }}
        >
            {props.children}
        </TareaContext.Provider>
    )
}

export default TareaState;
