import React, {useState, useContext} from 'react';
import { Link } from 'react-router-dom';
import AlertaContext from '../../context/alertas/alertaContext';
import AuthContext from '../../context/autenticacion/authContext';

const NuevaCuenta = () => {

    //extraer valores del context
    const alertaContext = useContext( AlertaContext );
    const { alerta, mostrarAlerta } = alertaContext;

    const authContext = useContext(AuthContext);
    const { registrarUsuario } = authContext

    //state para iniciar sesion
    const [usuario, guardarUsuario] = useState({
        nombre:'',
        email:'',
        password:'',
        confirmar:''
    });

    //extraer de usuario
    const {nombre, email, password, confirmar} = usuario;

    const onChange = e =>{
        guardarUsuario({
            ...usuario,
            [e.target.name]: e.target.value
        })
    }

    //cuando el usuario queira iniciar sesion
    const onSubmit = e =>{
        e.preventDefault();

        //validar que no haya campos vacios 
        if(nombre.trim() === '' || 
            email.trim() === '' || 
            password.trim() === '' || 
            confirmar.trim() === '' )
            {
                mostrarAlerta('Todos los campos son obligatorios', 'alerta-error');
                return;
        }

        //password minimo de 6 caracteres
        if(password.length < 6){
            mostrarAlerta('La contraseña debe tener por lo menos 6 carácteres', 'alerta-error');
            return;
        }

        //los 2 passwords iguales
        if(password !== confirmar){
            mostrarAlerta('Las contraseñas deben ser iguales', 'alerta-error');
            return;
        }

        //pasarlo al action (fuction definida en reducer)
        registrarUsuario({
            nombre,
            email,
            password
        })

    }


    return ( 
        <div className='form-usuario'>
            { alerta ?  
                (<div className={`alerta ${alerta.categoria}`}>{alerta.msg}</div>)
            : null}
            <div className='contenedor-form sombra-dark'>
                <h1>Crear Cuenta</h1>

                <form
                    onSubmit={onSubmit}
                >
                    <div className='campo-form'>
                        <label htmlFor='nombre'>Nombre </label>
                        <input
                            type='text'
                            id='nombre'
                            name='nombre'
                            placeholder='Nombre Completo'
                            value={nombre}
                            onChange={onChange}
                        />
                    </div>

                    <div className='campo-form'>
                        <label htmlFor='email'>Email </label>
                        <input
                            type='email'
                            id='email'
                            name='email'
                            placeholder='ejemplo@ejemplo.ej'
                            value={email}
                            onChange={onChange}
                        />
                    </div>

                    <div className='campo-form'>
                        <label htmlFor='password'>Password</label>
                        <input
                            type='password'
                            id='password'
                            name='password'
                            placeholder='Contraseña123'
                            value={password}
                            onChange={onChange}
                        />
                    </div>

                    <div className='campo-form'>
                        <label htmlFor='confirmar'>Confirmar Password</label>
                        <input
                            type='password'
                            id='confirmar'
                            name='confirmar'
                            placeholder='Contraseña123'
                            value={confirmar}
                            onChange={onChange}
                        />
                    </div>

                    <div className='campo form'>
                        <input 
                            type='submit' 
                            className='btn btn-primario btn-block' 
                            value='Registrarme'
                        />
                    </div>
                </form>
                <Link to={'/'}className='enlace-cuenta'>
                    Volver a Iniciar Sesion
                </Link>
            </div>
        </div>
     );
}
 
export default NuevaCuenta;
