import React, {useReducer} from 'react';
import TareaContext from './tareaContext';
import TareaReducer from './tareaReducer';
import {
    TAREAS_PROYECTO,
    AGREGAR_TAREA,
    VALIDAR_TAREA,
    ELIMINAR_TAREA,
    ESTADO_TAREA,
    TAREA_ACTUAL
} from '../../types';

const TareaState = props => {
    const initialState={
        tareas:[
            {id: 1, nombre: 'elegir platafomra', estado: true, proyectoId: 1},
            {id: 2, nombre: 'elegir colores', estado: false, proyectoId: 2},
            {id: 3, nombre: 'elegir lenguaje', estado: false, proyectoId: 3},
            {id: 4, nombre: 'elegir hosting', estado: true, proyectoId: 4},
            {id: 5, nombre: 'elegir platafomra', estado: true, proyectoId: 1},
            {id: 6, nombre: 'elegir colores', estado: false, proyectoId: 2},
            {id: 7, nombre: 'elegir lenguaje', estado: false, proyectoId: 3},
            {id: 8, nombre: 'elegir platafomra', estado: true, proyectoId: 4},
            {id: 9, nombre: 'elegir colores', estado: false, proyectoId: 1},
            {id: 10, nombre: 'elegir lenguaje', estado: false, proyectoId: 2}
        ],
        tareasproyecto: null,
        errortarea: false,
        tareaseleccionada: null
    }

    //Crear dispatch y state
    const [state, dispatch] = useReducer(TareaReducer, initialState);


    //CREAR LAS FUNCIONES QUE NECESITAMOS DE LAS TAREAS

    //Obtener las tareas de un proyecto 
    const obtenerTareas = proyectoId =>{
        dispatch({
            type: TAREAS_PROYECTO,
            payload: proyectoId
        })
    }

    // Anyadir una tarea al proyecto seleccionado
    const agregarTarea = tarea=>{
        dispatch({
            type: AGREGAR_TAREA,
            payload: tarea
        })
    }

    //Validar y mostrar un error en caso de ser necesario
    const validarTarea = () =>{
        dispatch({
            type: VALIDAR_TAREA

        })
    }

    //Eliminar las tareas por el id
    const eliminarTarea = id => {
        dispatch({
            type: ELIMINAR_TAREA,
            payload: id
        })
    }

    //Cambiar el estado de las tareas
    const cambiarEstadoTarea = tarea => {
        dispatch({
            type: ESTADO_TAREA,
            payload: tarea
        })
    }

    //Extraer una tarea para la edicion
    const guardarTareaActual = tarea =>{
        dispatch({
            type: TAREA_ACTUAL,
            payload : tarea
        })
    }

    return (
        <TareaContext.Provider
        value={{
            tareas: state.tareas,
            tareasproyecto : state.tareasproyecto,
            errortarea: state.errortarea,
            obtenerTareas,
            agregarTarea,
            validarTarea,
            eliminarTarea,
            cambiarEstadoTarea,
            guardarTareaActual
        }}
        >
            {props.children}
        </TareaContext.Provider>
    )
}

export default TareaState;
